import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom"

export default function Manage_Students() {

  const navigate = useNavigate();
  const location = useLocation();

  const [students, setStudents] = useState([])
  const [name, setName] = useState([])
  const [password, setPassword] = useState([])

  const fetchStudents = async () => {
    const res = await fetch(`/all_students`);
    const data = await res.json();
    setStudents(data);
  };

  const handleAdd = async () => {
    await fetch(`/student`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, password }),
    });
    setName('');
    setPassword('');
    fetchStudents();
  };

  const deleteStudent = async (id) => {
    try {
      const res = await fetch(`/student/${id}`, {
        method: "DELETE",
      });

      if (res.ok) {
        fetchStudents();
      } else {
        const text = await res.text();
        console.log(`Failed to delete student: ${text}`);
      }
    } catch (error) {
      console.error("Error deleting student:", error);
      alert("An error occurred while deleting the student.");
    }
  };

  useEffect(() => {
    fetchStudents();
    console.log(students)
  }, [])

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h2>Students</h2>

      <ul style={{ listStyle: "none", padding: 0 }}>
        {students.map((student) => (
          <li
            key={student.uuid}
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "space-between",
              padding: "8px 0",
              borderBottom: "1px solid #ddd",
            }}
          >
            <div style={{ flex: 1 }}>
              <strong>ðŸ‘¤ {student.name}</strong> â€” Rented Books: {student.rentedbooks}
            </div>
            <div style={{ display: "flex", gap: "10px" }}>
              <button style={{ padding: "4px 8px", cursor: "pointer" }} onClick={
                () => navigate(`/librarian/edit/student/${student.uuid}`, {
                  state: { studentName: student.name }
                })
              } >Edit</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );

}
