import { useEffect, useState } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";

export default function Edit_Students() {
  const { id } = useParams();
  const location = useLocation();

  const [requests, setRequests] = useState([]);
  const [borrowCount, setBorrowCount] = useState(0); // <-- new state
  const [activeInputId, setActiveInputId] = useState(null);
  const [daysInput, setDaysInput] = useState("");

  const studentName = location.state?.studentName;

  const fetchRequests = async () => {
    try {
      const res = await fetch(`/requests/${id}`);
      if (!res.ok) throw new Error("Failed to fetch students requests");
      const data = await res.json();
      setRequests(data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchBorrowCount = async () => {
    try {
      const res = await fetch(`/student/nob/${id}`);
      if (!res.ok) throw new Error("Failed to fetch borrow count");
      const data = await res.json();
      setBorrowCount(data); // assuming response is { count: number }
      console.log(borrowCount);
    } catch (err) {
      console.error(err);
    }
  };

  const handleAccept = async (requestid, days) => {
    try {
      const res = await fetch(`/accept_request/${requestid}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ days }),
      });
      if (!res.ok) throw new Error("Failed to accept request");

      setActiveInputId(null);
      setDaysInput("");
      fetchRequests();
      fetchBorrowCount(); // update after accepting
    } catch (err) {
      console.error(err);
    }
  };

  const handleSubmit = (requestId) => {
    const parsed = parseInt(daysInput, 10);
    if (isNaN(parsed)) {
      alert("Please enter a valid number");
      return;
    }
    handleAccept(requestId, parsed);
  };

  const handleCancel = () => {
    setActiveInputId(null);
    setDaysInput("");
  };

  useEffect(() => {
    fetchRequests();
    fetchBorrowCount(); // get borrow count on load
  }, [id]);

  return (
    <div>
      <h2>Student: {studentName}</h2>

      <h4>Pending Requests</h4>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {requests.map((request) => (
          <li
            key={request.uuid}
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "space-between",
              padding: "8px 0",
              borderBottom: "1px solid #ddd",
            }}
          >
            <div style={{ flex: 1 }}>
              <strong>{request.bookname}</strong>
            </div>

            {activeInputId === request.uuid ? (
              <div style={{ display: "flex", gap: "8px", alignItems: "center" }}>
                <input
                  type="number"
                  value={daysInput}
                  onChange={(e) => setDaysInput(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === "Enter") handleSubmit(request.uuid);
                    if (e.key === "Escape") handleCancel();
                  }}
                  placeholder="Days"
                  style={{ width: "60px" }}
                />
                <button onClick={() => handleSubmit(request.userid)}>OK</button>
                <button onClick={handleCancel}>Cancel</button>
              </div>
            ) : (
              <div style={{ display: "flex", gap: "10px" }}>
                {borrowCount < 3 && (
                  <button
                    style={{ padding: "4px 8px", cursor: "pointer" }}
                    onClick={() => setActiveInputId(request.uuid)}
                  >
                    Accept
                  </button>
                )}
                <button style={{ padding: "4px 8px", cursor: "pointer" }}>
                  Deny
                </button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
