import { useEffect, useState } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";

export default function Edit_Students() {
  const { id } = useParams();
  const location = useLocation();

  const [requests, setRequests] = useState([])
  const studentName = location.state?.studentName;

  const fetchRequests = async () => {
    try {
      const res = await fetch(`/requests/${id}`);
      if (!res.ok) {
        throw new Error("Failed to fetch students requests");
      }
      const data = await res.json();
      setRequests(data)
    } catch (err) {
      console.error(err);
    }
  };

  const handleAccept = async () => {
    try {
      const res = await fetch(`/accept_request/${requstid}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ days }),
      });

      if (!res.ok) {
        throw new Error("Failed to accept request");
      }
      const data = await res.json();
      setRequests(data)
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchRequests(id);
  }, [id]);

  return (
    <div>
      <h2>Student: {studentName}</h2>

      <h4>Pending Requests</h4>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {requests.map((request) => (
          <li
            key={request.uuid}
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "space-between",
              padding: "8px 0",
              borderBottom: "1px solid #ddd",
            }}
          >
            <div style={{ flex: 1 }}>
              <strong>{request.bookname}</strong>
            </div>
            <div style={{ display: "flex", gap: "10px" }}>
              <button style={{ padding: "4px 8px", cursor: "pointer" }}>Accept</button>
              <button style={{ padding: "4px 8px", cursor: "pointer" }}>Deny</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
